<body>
    <h2>Mapa Interactivo</h2>
    <div id="map"></div>

    <script>
        function initMap() {
            var location = { lat: -92.63931, lng: 16.72734 }; // Ubicación de ejemplo
            var map = new google.maps.Map(document.getElementById('map'), {
                zoom: 14,
                center: location
            });
            var marker = new google.maps.Marker({
                position: location,
                map: map
            });
        }
    </script>
</body>

